///
//  Send a get request to the url, headers is optional.
//  Use userp to identify response when making multiple requests simultaneously.
int QmlZhttpClient::get (const QString &url, QmlZlistx *headers, void *userp) {
    return zhttp_client_get (self, url.toUtf8().data(), headers->self, userp);
};

///
//  Receive the response for one of the requests. Blocks until a response is ready.
//  Use userp to identify the request.
int QmlZhttpClient::recv (int *responseCode, QmlZchunk *data, void **userp) {
    return zhttp_client_recv (self, responseCode, &data->self, userp);
};


